UPDATE `notification` SET `body` = 
'If you have problems viewing this page, please click <a href="{WEB_MAIL_URL}">here</a>.\r\nThis message is not a spam. You registered with <a href="http://polishdate.com">PolishDate.com</a> from computer {REGISTRATION_IP} on {CREATED_AT}, using {EMAIL}. \r\n-----------------------------------------------------------------------------\r\nHi {Username}!\r\n\r\nThere was some activity on your profile which you may want to know about:\r\n\r\n<strong>{NB_MESSAGES} new messages</strong>. You have {NB_MESSAGES} unread message(s). See <a href="{URL_TO_MESSAGES}" class="actions">your new message(s)</a>.\r\n\r\nYou have {NB_WINKS} new wink(s) that you have not viewed yet. See <a href="{URL_TO_WINKS}" class="actions">who winked you</a>.\r\n\r\nYou have been added to the hot-list by {NB_HOTLIST} new user(s), since you last viewed "who hot-listed me" page. See <a href="{URL_TO_HOTLIST}" class="actions">who hot-listed you</a>.\r\n\r\nYour profile have been viewed by {NB_PROFILE_VIEWES} new member(s) since your last review. See <a href="{URL_TO_PROFILE_VIEWES}" class="actions">who viewed your profile</a>.\r\n\r\nOr just <a href="http://polishdate.com/en/signin.html" class="actions">log into your account</a> and see all the recent activity.\r\n\r\nGood Luck\r\n\r\n<a href="http://www.polishdate.com" class="actions">PolishDate.com</a>\r\n<br />'
WHERE `notification`.`id` =9 AND `notification`.`cat_id` =1;

UPDATE `notification` SET `body` = 
'Ta wiadomość nie jest spamem. Zarejestrowałeś się na <a href="http://www.szukammilionera.com">SzukamMilionera.com</a> (lub na <a href="http://www.lovestreet.pl">LoveStreet.pl</a>) dnia {CREATED_AT} z komputera {REGISTRATION_IP}, używając adresu {EMAIL}.\r\n-----------------------------------------------------------------------------\r\nCześć {Username}!\r\n\r\nTwój profil był przedmiotem zainteresowania, pomyśleliśmy więc, że chciał(a)byś o tym wiedzieć:\r\n\r\nMasz <strong>{NB_MESSAGES} nowe wiadomości</strong>. Zobacz <a href="{URL_TO_MESSAGES}" class="actions">kto o tobie pamięta</a>!.\r\n\r\nMasz <strong>{NB_WINKS} nowe pozdrowienia</strong>. Zobacz <a href="{URL_TO_WINKS}" class="actions">kto chce z tobą nawiązać znajomość</a>!\r\n\r\n<strong>{NB_HOTLIST} użytkownik(ów)</strong> dodał(o) cię do gorącej listy. Zobacz <a href="{URL_TO_HOTLIST}" class="actions">dla kogo jesteś "hot"</a>.\r\n\r\n<strong>{NB_PROFILE_VIEWES} użytkownik(ów)</strong> obejrzało twój profil. Sprawdź <a href="{URL_TO_PROFILE_VIEWES}" class="actions">kto jest twoim cichym wielbicielem</a>.\r\n\r\nKliknij <a href="http:www.//szukammilionera.com/signin.html" class="actions">tu</a> aby się zalogować.\r\n\r\nPowodzenia!\r\n\r\nMonika | <a href="http://www.SzukamMilionera.com">SzukamMilionera.com</a>\r\n<br />'
WHERE `notification`.`id` =9 AND `notification`.`cat_id` =2;